import{_ as t,o as a,c as i,a as p}from"./app.ddfdc32e.js";const e={},T=p('<h1 id="\u3010http\u534F\u8BAE\u3011\u53D1\u5C55\u5386\u7A0B" tabindex="-1"><a class="header-anchor" href="#\u3010http\u534F\u8BAE\u3011\u53D1\u5C55\u5386\u7A0B" aria-hidden="true">#</a> \u3010HTTP\u534F\u8BAE\u3011\u53D1\u5C55\u5386\u7A0B</h1><p>[TOC]</p><h2 id="http-0-9" tabindex="-1"><a class="header-anchor" href="#http-0-9" aria-hidden="true">#</a> HTTP/0.9</h2><p>\u534F\u8BAE\u5B9A\u4E49\u4E86\u5BA2\u6237\u7AEF\u53D1\u8D77\u8BF7\u6C42\u3001\u670D\u52A1\u7AEF\u54CD\u5E94\u8BF7\u6C42\u7684\u901A\u4FE1\u6A21\u5F0F\u3002\u8BF7\u6C42\u62A5\u6587\u5185\u5BB9\u53EA\u6709 1 \u884C\uFF0C\u4E3A GET \u52A0\u4E0A\u8BF7\u6C42\u7684\u6587\u4EF6\u8DEF\u5F84\u3002\u670D\u52A1\u7AEF\u6536\u5230\u8BF7\u6C42\u540E\u8FD4\u56DE\u4E00\u4E2A\u4EE5 ASCII \u5B57\u7B26\u6D41\u7F16\u7801\u7684 HTML \u6587\u6863\u3002</p><p>\u53EA\u5141\u8BB8\u7528\u201CGET\u201D\u52A8\u4F5C\u4ECE\u670D\u52A1\u5668\u4E0A\u83B7\u53D6 HTML \u6587\u6863\uFF0C\u5E76\u4E14\u5728\u54CD\u5E94\u8BF7\u6C42\u4E4B\u540E\u7ACB\u5373\u5173\u95ED\u8FDE\u63A5\uFF0C\u529F\u80FD\u975E\u5E38\u6709\u9650\u3002</p><p><img src="https://doc.panjingyi.top/blog/202207012116863.jpg" alt=""></p><h2 id="http-1-0" tabindex="-1"><a class="header-anchor" href="#http-1-0" aria-hidden="true">#</a> HTTP/1.0</h2><ol><li>\u589E\u52A0\u4E86 HEAD\u3001POST \u7B49\u65B0\u65B9\u6CD5\uFF1B</li><li>\u589E\u52A0\u4E86\u54CD\u5E94\u72B6\u6001\u7801\uFF0C\u6807\u8BB0\u53EF\u80FD\u7684\u9519\u8BEF\u539F\u56E0\uFF1B</li><li>\u5F15\u5165\u4E86\u534F\u8BAE\u7248\u672C\u53F7\u6982\u5FF5\uFF1B</li><li>\u5F15\u5165\u4E86 HTTP Header\uFF08\u5934\u90E8\uFF09\u7684\u6982\u5FF5\uFF0C\u8BA9 HTTP \u5904\u7406\u8BF7\u6C42\u548C\u54CD\u5E94\u66F4\u52A0\u7075\u6D3B\uFF1B</li><li>\u4F20\u8F93\u7684\u6570\u636E\u4E0D\u518D\u4EC5\u9650\u4E8E\u6587\u672C\u3002</li></ol><p>\u6700\u6838\u5FC3\u7684\u6539\u53D8\u662F<strong>\u589E\u52A0\u4E86\u5934\u90E8\u8BBE\u5B9A</strong>\uFF0C\u5934\u90E8\u5185\u5BB9\u4EE5\u952E\u503C\u5BF9\u7684\u5F62\u5F0F\u8BBE\u7F6E\u3002\u8BF7\u6C42\u5934\u90E8\u901A\u8FC7 Accept \u5B57\u6BB5\u6765\u544A\u8BC9\u670D\u52A1\u7AEF\u53EF\u4EE5\u63A5\u6536\u7684\u6587\u4EF6\u7C7B\u578B\uFF0C\u54CD\u5E94\u5934\u90E8\u518D\u901A\u8FC7 Content-Type \u5B57\u6BB5\u6765\u544A\u8BC9\u6D4F\u89C8\u5668\u8FD4\u56DE\u6587\u4EF6\u7684\u7C7B\u578B\u3002</p><p><img src="https://doc.panjingyi.top/blog/202207012116864.jpg" alt=""></p><h2 id="http-1-1" tabindex="-1"><a class="header-anchor" href="#http-1-1" aria-hidden="true">#</a> HTTP/1.1</h2><p>HTTP/1.0 \u6BCF\u8FDB\u884C\u4E00\u6B21\u901A\u4FE1\uFF0C\u90FD\u9700\u8981\u7ECF\u5386<strong>\u5EFA\u7ACB\u8FDE\u63A5\u3001\u4F20\u8F93\u6570\u636E\u548C\u65AD\u5F00\u8FDE\u63A5</strong>\u4E09\u4E2A\u9636\u6BB5\u3002\u5F53\u4E00\u4E2A\u9875\u9762\u5F15\u7528\u4E86\u8F83\u591A\u7684\u5916\u90E8\u6587\u4EF6\u65F6\uFF0C\u8FD9\u4E2A\u5EFA\u7ACB\u8FDE\u63A5\u548C\u65AD\u5F00\u8FDE\u63A5\u7684\u8FC7\u7A0B\u5C31\u4F1A\u589E\u52A0\u5927\u91CF\u7F51\u7EDC\u5F00\u9500\u3002</p><p>\u4E3A\u4E86\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\uFF0C1999 \u5E74\u63A8\u51FA\u7684 HTTP/1.1 \u7248\u672C\u589E\u52A0\u4E86\u4E00\u4E2A<strong>\u521B\u5EFA\u6301\u4E45\u8FDE\u63A5</strong>\u7684\u65B9\u6CD5\u3002\u4E3B\u8981\u5B9E\u73B0\u662F\u5F53\u4E00\u4E2A\u8FDE\u63A5\u4F20\u8F93\u5B8C\u6210\u65F6\uFF0C\u5E76\u4E0D\u662F\u9A6C\u4E0A\u8FDB\u884C\u5173\u95ED\uFF0C\u800C\u662F\u7EE7\u7EED\u590D\u7528\u5B83\u4F20\u8F93\u5176\u4ED6\u8BF7\u6C42\u7684\u6570\u636E\uFF0C\u8FD9\u4E2A\u8FDE\u63A5\u4FDD\u6301\u5230\u6D4F\u89C8\u5668\u6216\u8005\u670D\u52A1\u5668\u8981\u6C42\u65AD\u5F00\u8FDE\u63A5\u4E3A\u6B62\u3002</p><p>HTTP/1.1 \u662F\u5BF9 HTTP/1.0 \u7684\u5C0F\u5E45\u5EA6\u4FEE\u6B63\u3002</p><p>HTTP/1.1 \u4E3B\u8981\u7684\u53D8\u66F4\u70B9\u6709\uFF1A</p><ol><li>\u589E\u52A0\u4E86 PUT\u3001DELETE \u7B49\u65B0\u7684\u65B9\u6CD5\uFF1B</li><li>\u589E\u52A0\u4E86\u7F13\u5B58\u7BA1\u7406\u548C\u63A7\u5236\uFF1B</li><li>\u660E\u786E\u4E86\u8FDE\u63A5\u7BA1\u7406\uFF0C\u5141\u8BB8\u6301\u4E45\u8FDE\u63A5\uFF1B</li><li>\u5141\u8BB8\u54CD\u5E94\u6570\u636E\u5206\u5757\uFF08chunked\uFF09\uFF0C\u5229\u4E8E\u4F20\u8F93\u5927\u6587\u4EF6\uFF1B</li><li>\u5F3A\u5236\u8981\u6C42 Host \u5934\uFF0C\u8BA9\u4E92\u8054\u7F51\u4E3B\u673A\u6258\u7BA1\u6210\u4E3A\u53EF\u80FD\u3002</li></ol><h3 id="\u5EF6\u4F38-tcp-\u662F\u600E\u6837\u5EFA\u7ACB-\u65AD\u5F00\u8FDE\u63A5\u7684" tabindex="-1"><a class="header-anchor" href="#\u5EF6\u4F38-tcp-\u662F\u600E\u6837\u5EFA\u7ACB-\u65AD\u5F00\u8FDE\u63A5\u7684" aria-hidden="true">#</a> \u5EF6\u4F38 \uFF1ATCP \u662F\u600E\u6837\u5EFA\u7ACB/\u65AD\u5F00\u8FDE\u63A5\u7684\uFF1F</h3><p>\u56E0\u4E3A HTTP \u662F\u57FA\u4E8E TCP \u5B9E\u73B0\u7684\uFF0C\u6240\u4EE5\u8FD9\u91CC\u6269\u5C55\u4E00\u4E0B TCP \u5EFA\u7ACB\u8FDE\u63A5\u4EE5\u53CA\u65AD\u5F00\u8FDE\u63A5\u7684\u8FC7\u7A0B\uFF0C\u4E5F\u5C31\u662F\u5E38\u5E38\u88AB\u63D0\u7684\u201C\u4E09\u6B21\u63E1\u624B\u201D\u548C\u201C\u56DB\u6B21\u6325\u624B\u201D\u3002</p><p>\u70B9\u51FB\u8FD9\u91CC\u{1F449}<a href="https://blog.csdn.net/weixin_52834435/article/details/123778742?spm=1001.2014.3001.5501" target="_blank" rel="noopener noreferrer">\u3010TCP\u534F\u8BAE\u3011TCP \u4E3A\u4EC0\u4E48\u63E1\u624B\u662F 3 \u6B21\u3001\u6325\u624B\u662F 4 \u6B21\uFF1F</a></p><h2 id="http-2" tabindex="-1"><a class="header-anchor" href="#http-2" aria-hidden="true">#</a> HTTP/2</h2><p>HTTP/1.1 \u867D\u7136\u901A\u8FC7\u957F\u8FDE\u63A5\u51CF\u5C11\u4E86\u5927\u91CF\u521B\u5EFA/\u65AD\u5F00\u8FDE\u63A5\u9020\u6210\u7684\u6027\u80FD\u6D88\u8017\uFF0C\u4F46\u7531\u4E8E\u5B83\u7684\u5E76\u53D1\u80FD\u529B\u53D7\u5230\u9650\u5236\uFF0C\u6240\u4EE5\u4F20\u8F93\u6027\u80FD\u8FD8\u6709\u5F88\u5927\u63D0\u5347\u7A7A\u95F4\u3002</p><p>\u4E3A\u4EC0\u4E48\u8BF4 HTTP/1.1 \u7684\u5E76\u53D1\u80FD\u529B\u53D7\u9650\u5462\uFF1F\u4E3B\u8981\u8868\u73B0\u5728\u4E24\u4E2A\u65B9\u9762\uFF1A</p><ul><li><p><strong>\u6D4F\u89C8\u5668\u4E3A\u4E86\u51CF\u8F7B\u670D\u52A1\u5668\u7684\u538B\u529B</strong>\uFF0C\u9650\u5236\u4E86\u540C\u4E00\u4E2A\u57DF\u4E0B\u7684 HTTP \u8FDE\u63A5\u6570\uFF0C\u5373 6 ~ 8 \u4E2A\uFF0C\u6240\u4EE5\u5728 HTTP/1.1 \u4E0B\u5F88\u5BB9\u6613\u770B\u5230\u8D44\u6E90\u6587\u4EF6\u7B49\u5F85\u52A0\u8F7D\u7684\u60C5\u51B5\uFF0C\u5BF9\u5E94\u4F18\u5316\u7684\u65B9\u5F0F\u5C31\u662F\u4F7F\u7528\u591A\u4E2A\u57DF\u540D\u6765\u52A0\u8F7D\u56FE\u7247\u8D44\u6E90\uFF1B</p></li><li><p><strong>HTTP/1.1 \u672C\u8EAB\u7684\u95EE\u9898</strong>\uFF0C\u867D\u7136 HTTP/1.1 \u4E2D\u4F7F\u7528\u6301\u4E45\u8FDE\u63A5\u65F6\uFF0C\u591A\u4E2A\u8BF7\u6C42\u80FD\u5171\u7528\u4E00\u4E2A TCP \u8FDE\u63A5\uFF0C\u4F46\u5728\u4E00\u4E2A\u8FDE\u63A5\u4E2D\u540C\u4E00\u65F6\u523B\u53EA\u80FD\u5904\u7406\u4E00\u4E2A\u8BF7\u6C42\uFF0C\u5728\u5F53\u524D\u7684\u8BF7\u6C42\u6CA1\u6709\u7ED3\u675F\u4E4B\u524D\uFF0C\u5176\u4ED6\u7684\u8BF7\u6C42\u53EA\u80FD\u5904\u4E8E\u963B\u585E\u72B6\u6001\uFF0C\u8FD9\u79CD\u60C5\u51B5\u88AB\u79F0\u4E3A \u201C<strong>\u961F\u5934\u963B\u585E</strong>\u201D \u3002</p></li></ul><p>\u5728 2015 \u5E74\u6B63\u5F0F\u53D1\u5E03\u7684 <strong>HTTP/2 \u4E2D\u65B0\u589E\u4E86\u4E00\u4E2A\u4E8C\u8FDB\u5236\u5206\u5E27\u7684\u673A\u5236\u6765\u63D0\u5347\u4F20\u8F93\u6548\u7387</strong>\u3002</p><p>HTTP/2 \u5C06\u9ED8\u8BA4\u4E0D\u518D\u4F7F\u7528 ASCII \u7F16\u7801\u4F20\u8F93\uFF0C\u800C\u662F\u6539\u4E3A\u4E8C\u8FDB\u5236\u6570\u636E\u3002\u5BA2\u6237\u7AEF\u5728\u53D1\u9001\u8BF7\u6C42\u65F6\u4F1A\u5C06\u6BCF\u4E2A\u8BF7\u6C42\u7684\u5185\u5BB9\u5C01\u88C5\u6210\u4E0D\u540C\u7684\u5E26\u6709\u7F16\u53F7\u7684\u4E8C\u8FDB\u5236\u5E27\uFF0C\u7136\u540E\u5C06\u8FD9\u4E9B\u5E27\u540C\u65F6\u53D1\u9001\u7ED9\u670D\u52A1\u7AEF\u3002\u670D\u52A1\u7AEF\u63A5\u6536\u5230\u6570\u636E\u4E4B\u540E\uFF0C\u4F1A\u5C06\u76F8\u540C\u7F16\u53F7\u7684\u5E27\u5408\u5E76\u4E3A\u5B8C\u6574\u7684\u8BF7\u6C42\u4FE1\u606F\u3002\u540C\u6837\uFF0C\u670D\u52A1\u7AEF\u8FD4\u56DE\u7ED3\u679C\u3001\u5BA2\u6237\u7AEF\u63A5\u6536\u7ED3\u679C\u4E5F\u9075\u5FAA\u8FD9\u4E2A\u5E27\u7684\u62C6\u5206\u4E0E\u7EC4\u5408\u7684\u8FC7\u7A0B\u3002</p><p>\u53D7\u76CA\u4E8E\u4E8C\u8FDB\u5236\u5206\u5E27\uFF0C\u5BF9\u4E8E\u540C\u4E00\u4E2A\u57DF\uFF0C\u5BA2\u6237\u7AEF\u53EA\u9700\u8981\u4E0E\u670D\u52A1\u7AEF\u5EFA\u7ACB\u4E00\u4E2A\u8FDE\u63A5\u5373\u53EF\u5B8C\u6210\u901A\u4FE1\u9700\u6C42\uFF0C\u81EA\u7136\u4E5F\u4E0D\u518D\u53D7\u9650\u4E8E\u6D4F\u89C8\u5668\u7684\u8FDE\u63A5\u6570\u9650\u5236\u4E86\uFF0C\u8FD9\u79CD\u5229\u7528\u4E00\u4E2A\u8FDE\u63A5\u6765\u53D1\u9001\u591A\u4E2A\u8BF7\u6C42\u7684\u65B9\u5F0F\u79F0\u4E3A\u201C<strong>\u591A\u8DEF\u590D\u7528</strong>\u201D\u3002</p><p><img src="https://doc.panjingyi.top/blog/202207012116865.jpg" alt=""></p><p>HTTP/2 \u7684\u5236\u5B9A\u5145\u5206\u8003\u8651\u4E86\u73B0\u4ECA\u4E92\u8054\u7F51\u7684\u73B0\u72B6\uFF1A\u5BBD\u5E26\u3001\u79FB\u52A8\u3001\u4E0D\u5B89\u5168\uFF0C\u5728\u9AD8\u5EA6\u517C\u5BB9HTTP/1.1 \u7684\u540C\u65F6\u5728\u6027\u80FD\u6539\u5584\u65B9\u9762\u505A\u4E86\u5F88\u5927\u52AA\u529B\uFF0C\u4E3B\u8981\u7684\u7279\u70B9\u6709\uFF1A</p><ol><li>\u4E8C\u8FDB\u5236\u534F\u8BAE\uFF0C\u4E0D\u518D\u662F\u7EAF\u6587\u672C\uFF1B</li><li>\u53EF\u53D1\u8D77\u591A\u4E2A\u8BF7\u6C42\uFF0C\u5E9F\u5F03\u4E86 1.1 \u91CC\u7684\u7BA1\u9053\uFF1B</li><li>\u4F7F\u7528\u4E13\u7528\u7B97\u6CD5\u538B\u7F29\u5934\u90E8\uFF0C\u51CF\u5C11\u6570\u636E\u4F20\u8F93\u91CF\uFF1B</li><li>\u5141\u8BB8\u670D\u52A1\u5668\u4E3B\u52A8\u5411\u5BA2\u6237\u7AEF\u63A8\u9001\u6570\u636E\uFF1B</li><li>\u589E\u5F3A\u4E86\u5B89\u5168\u6027\uFF0C\u201C\u4E8B\u5B9E\u4E0A\u201D\u8981\u6C42\u52A0\u5BC6\u901A\u4FE1\u3002</li></ol><h2 id="http-3" tabindex="-1"><a class="header-anchor" href="#http-3" aria-hidden="true">#</a> HTTP/3</h2><p>HTTP/2 \u4E5F\u5E76\u975E\u5B8C\u7F8E\uFF0C\u8003\u8651\u4E00\u79CD\u60C5\u51B5\uFF0C\u5982\u679C\u5BA2\u6237\u7AEF\u6216\u670D\u52A1\u7AEF\u5728\u901A\u4FE1\u65F6\u51FA\u73B0\u6570\u636E\u5305\u4E22\u5931\uFF0C\u6216\u8005\u4EFB\u4F55\u4E00\u65B9\u7684\u7F51\u7EDC\u51FA\u73B0\u4E2D\u65AD\uFF0C\u90A3\u4E48\u6574\u4E2A TCP \u8FDE\u63A5\u5C31\u4F1A\u6682\u505C\u3002</p><p>HTTP/2 \u7531\u4E8E\u91C7\u7528\u4E8C\u8FDB\u5236\u5206\u5E27\u8FDB\u884C\u591A\u8DEF\u590D\u7528\uFF0C\u901A\u5E38\u53EA\u4F7F\u7528\u4E00\u4E2A TCP \u8FDE\u63A5\u8FDB\u884C\u4F20\u8F93\uFF0C\u5728\u4E22\u5305\u6216\u7F51\u7EDC\u4E2D\u65AD\u7684\u60C5\u51B5\u4E0B\u540E\u9762\u7684\u6240\u6709\u6570\u636E\u90FD\u88AB\u963B\u585E\u3002\u4F46\u5BF9\u4E8E HTTP/1.1 \u6765\u8BF4\uFF0C\u53EF\u4EE5\u5F00\u542F\u591A\u4E2A TCP \u8FDE\u63A5\uFF0C\u4EFB\u4F55\u4E00\u4E2A TCP \u51FA\u73B0\u95EE\u9898\u90FD\u4E0D\u4F1A\u5F71\u54CD\u5176\u4ED6 TCP \u8FDE\u63A5\uFF0C\u5269\u4F59\u7684 TCP \u8FDE\u63A5\u8FD8\u53EF\u4EE5\u6B63\u5E38\u4F20\u8F93\u6570\u636E\u3002\u8FD9\u79CD\u60C5\u51B5\u4E0B HTTP/2 \u7684\u8868\u73B0\u5C31\u4E0D\u5982 HTTP/1 \u4E86\u3002</p><p>2018 \u5E74 HTTP/3 \u5C06\u5E95\u5C42\u4F9D\u8D56\u7684 TCP \u6539\u6210 UDP\uFF0C\u4ECE\u800C\u5F7B\u5E95\u89E3\u51B3\u4E86\u8FD9\u4E2A\u95EE\u9898\u3002UDP \u76F8\u5BF9\u4E8E TCP \u800C\u8A00\u6700\u5927\u7684\u7279\u70B9\u662F\u4F20\u8F93\u6570\u636E\u65F6\u4E0D\u9700\u8981\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u53EF\u4EE5\u540C\u65F6\u53D1\u9001\u591A\u4E2A\u6570\u636E\u5305\uFF0C\u6240\u4EE5\u4F20\u8F93\u6548\u7387\u5F88\u9AD8\uFF0C\u7F3A\u70B9\u5C31\u662F\u6CA1\u6709\u786E\u8BA4\u673A\u5236\u6765\u4FDD\u8BC1\u5BF9\u65B9\u4E00\u5B9A\u80FD\u6536\u5230\u6570\u636E\u3002</p><h2 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h2><p><img src="https://doc.panjingyi.top/blog/202207012116866.jpg" alt=""></p>',35),r=[T];function h(l,n){return a(),i("div",null,r)}var s=t(e,[["render",h],["__file","\u3010HTTP\u534F\u8BAE\u3011\u53D1\u5C55\u5386\u7A0B.html.vue"]]);export{s as default};
