import{_ as e,o as i,c as r,a as o}from"./app.988effc2.js";const t={},s=o('<h1 id="\u6D4F\u89C8\u5668\u5B89\u5168-xss\u653B\u51FB\u4E0Ecsrf\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#\u6D4F\u89C8\u5668\u5B89\u5168-xss\u653B\u51FB\u4E0Ecsrf\u653B\u51FB" aria-hidden="true">#</a> \u6D4F\u89C8\u5668\u5B89\u5168\uFF1AXSS\u653B\u51FB\u4E0ECSRF\u653B\u51FB</h1><h2 id="xss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#xss\u653B\u51FB" aria-hidden="true">#</a> XSS\u653B\u51FB</h2><h3 id="_1-\u4EC0\u4E48\u662Fxss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662Fxss\u653B\u51FB" aria-hidden="true">#</a> 1.\u4EC0\u4E48\u662FXSS\u653B\u51FB\uFF1F</h3><p><strong>\u6982\u5FF5</strong></p><p>XSS\u5168\u7A0B\u662FCross Site Scripting,\u4E3A\u4E86\u548C&quot;CSS&quot;\u533A\u5206\u5F00\u6765\uFF0C\u6545\u7B80\u79F0XSS\u3002\u7FFB\u8BD1\u8FC7\u6765\u5C31\u662F&quot;\u8DE8\u7AD9\u811A\u672C&quot;\u3002XSS\u653B\u51FB\u662F\u6307\u9ED1\u5BA2\u5F80HTML\u6587\u4EF6\u4E2D\u6216DOM\u4E2D\u6CE8\u5165\u6076\u610F\u811A\u672C\uFF0C\u4ECE\u800C\u5728\u7528\u6237\u6D4F\u89C8\u9875\u9762\u65F6\uFF0C\u5229\u7528<strong>\u6CE8\u5165\u7684\u6076\u610F\u811A\u672C\u5BF9\u7528\u6237\u5B9E\u65BD\u653B\u51FB\u7684\u4E00\u79CD\u624B\u6BB5</strong>\u3002</p><p>\u6700\u5F00\u59CB\u7684\u65F6\u5019\uFF0C\u8FD9\u79CD\u653B\u51FB\u662F\u901A\u8FC7\u8DE8\u57DF\u5B9E\u73B0\u7684\uFF0C\u6240\u4EE5\u53EB&quot;\u8DE8\u57DF\u811A\u672C&quot;\u3002\u4F46\u662F\u53D1\u5C55\u5230\u5982\u4ECA\uFF0C\u5F80HTML\u6587\u4EF6\u6CE8\u5165\u6076\u610F\u4EE3\u7801\u7684\u65B9\u5F0F\u8D8A\u6765\u8D8A\u591A\uFF0C\u6240\u4EE5\u662F\u5426\u8DE8\u57DF\u6CE8\u5165\u811A\u672C\u5DF2\u7ECF\u4E0D\u662F\u552F\u4E00\u7684\u6CE8\u5165\u624B\u6BB5\u4E86\uFF0C\u4F46\u662FXSS\u8FD9\u4E2A\u540D\u5B57\u5374\u4E00\u76F4\u4FDD\u7559\u81F3\u4ECA\u3002</p><p>XSS \u7684\u672C\u8D28\u662F\u56E0\u4E3A\u7F51\u7AD9\u6CA1\u6709\u5BF9\u6076\u610F\u4EE3\u7801\u8FDB\u884C\u8FC7\u6EE4\uFF0C\u4E0E\u6B63\u5E38\u7684\u4EE3\u7801\u6DF7\u5408\u5728\u4E00\u8D77\u4E86\uFF0C\u6D4F\u89C8\u5668\u6CA1\u6709\u529E\u6CD5\u5206\u8FA8\u54EA\u4E9B\u811A\u672C\u662F\u53EF\u4FE1\u7684\uFF0C\u4ECE\u800C\u5BFC\u81F4\u4E86\u6076\u610F\u4EE3\u7801\u7684\u6267\u884C\u3002</p><p>\u653B\u51FB\u8005\u53EF\u4EE5\u901A\u8FC7\u8FD9\u79CD\u653B\u51FB\u65B9\u5F0F\u53EF\u4EE5\u8FDB\u884C\u4EE5\u4E0B\u64CD\u4F5C\uFF1A</p><ul><li><p>\u83B7\u53D6\u9875\u9762\u7684\u6570\u636E\uFF0C\u5982DOM\u3001cookie\u3001localStorage\uFF1B</p></li><li><p>DOS\u653B\u51FB\uFF0C\u53D1\u9001\u5408\u7406\u8BF7\u6C42\uFF0C\u5360\u7528\u670D\u52A1\u5668\u8D44\u6E90\uFF0C\u4ECE\u800C\u4F7F\u7528\u6237\u65E0\u6CD5\u8BBF\u95EE\u670D\u52A1\u5668\uFF1B</p></li><li><p>\u7834\u574F\u9875\u9762\u7ED3\u6784\uFF1B</p></li><li><p>\u6D41\u91CF\u52AB\u6301\uFF08\u5C06\u94FE\u63A5\u6307\u5411\u67D0\u7F51\u7AD9\uFF09\uFF1B</p></li><li><p>\u53EF\u4EE5<strong>\u7A83\u53D6Cookie\u4FE1\u606F</strong>\uFF0C\u6076\u610F\u4EE3\u7801\u901A\u8FC7document.cookie\u83B7\u53D6Cookie\u4FE1\u606F\uFF0C\u7136\u540E\u901A\u8FC7XMLHttpRequest\u52A0\u4E0ACors\u529F\u80FD\u5C06\u6570\u636E\u53D1\u9001\u7ED9\u6076\u610F\u670D\u52A1\u5668\u3002\u6076\u610F\u670D\u52A1\u5668\u62FF\u5230\u7528\u6237\u7684Cookie\u4FE1\u606F\u4E4B\u540E\uFF0C\u5C31\u53EF\u4EE5\u5728\u5176\u4ED6\u7535\u8111\u4E0A\u6A21\u62DF\u7528\u6237\u7684\u767B\u5F55\uFF0C\u7136\u540E\u8FDB\u884C\u8F6C\u8D26\u7B49\u64CD\u4F5C\u3002</p></li><li><p>\u53EF\u4EE5<strong>\u76D1\u542C\u7528\u6237\u884C\u4E3A</strong>\uFF0C\u6076\u610FJavaScript\u53EF\u4EE5\u4F7F\u7528&quot;addEventListener&quot;\u63A5\u53E3\u76D1\u542C\u952E\u76D8\u4E8B\u4EF6\uFF0C\u6BD4\u5982\u53EF\u4EE5\u83B7\u53D6\u7528\u6237\u8F93\u5165\u7684\u4FE1\u7528\u5361\u7B49\u4FE1\u606F\uFF0C\u5C06\u5176\u53D1\u9001\u5230\u6076\u610F\u670D\u52A1\u5668\u3002\u9ED1\u5BA2\u638C\u63E1\u4E86\u8FD9\u4E9B\u4FE1\u606F\u4E4B\u540E\uFF0C\u53C8\u53EF\u4EE5\u505A\u5F88\u591A\u8FDD\u6CD5\u7684\u4E8B\u60C5\u3002</p></li><li><p>\u53EF\u4EE5\u901A\u8FC7<strong>\u4FEE\u6539DOM</strong>\uFF0C\u4F2A\u9020\u5047\u7684\u767B\u5F55\u7A97\u53E3\uFF0C\u7528\u6765\u6B3A\u9A97\u7528\u6237\u8F93\u5165\u7528\u6237\u540D\u548C\u5BC6\u7801\u7B49\u4FE1\u606F\u3002</p></li><li><p>\u8FD8\u53EF\u4EE5<strong>\u5728\u9875\u9762\u5185\u751F\u6210\u6D6E\u7A97\u5E7F\u544A</strong>\uFF0C\u8FD9\u4E9B\u5E7F\u544A\u4F1A\u4E25\u91CD\u7684\u5F71\u54CD\u7528\u6237\u4F53\u9A8C\u3002</p></li></ul><h3 id="_2-\u653B\u51FB\u7C7B\u578B" tabindex="-1"><a class="header-anchor" href="#_2-\u653B\u51FB\u7C7B\u578B" aria-hidden="true">#</a> 2.\u653B\u51FB\u7C7B\u578B</h3><p>\u5E38\u89C1\u7684\u6076\u610F\u811A\u672C\u6CE8\u5165\u6709\u4EE5\u4E0B\u51E0\u79CD\uFF0C\u5B58\u50A8\u578BXSS\u653B\u51FB\uFF0C\u53CD\u5C04\u578BXSS\u653B\u51FB\uFF0C\u57FA\u4E8EDOM\u7684XSS\u653B\u51FB\u3002</p><h5 id="_1-\u5B58\u50A8\u578Bxss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_1-\u5B58\u50A8\u578Bxss\u653B\u51FB" aria-hidden="true">#</a> 1\uFF09\u5B58\u50A8\u578BXSS\u653B\u51FB</h5><p>\u5B58\u50A8\u578B\u6307\u7684\u662F\u6076\u610F\u811A\u672C\u4F1A\u5B58\u50A8\u5728\u76EE\u6807\u670D\u52A1\u5668\u4E0A\uFF0C\u5F53\u6D4F\u89C8\u5668\u8BF7\u6C42\u6570\u636E\u65F6\uFF0C\u811A\u672C\u4ECE\u670D\u52A1\u5668\u4F20\u56DE\u5E76\u6267\u884C\u3002</p><p>\u8FD9\u79CD\u653B\u51FB\u5E38\u2EC5\u4E8E\u5E26\u6709\u2F64\u6237\u4FDD\u5B58\u6570\u636E\u7684\u2F79\u7AD9\u529F\u80FD\uFF0C\u5982\u8BBA\u575B\u53D1\u5E16\u3001\u5546\u54C1\u8BC4\u8BBA\u3001\u2F64\u6237\u79C1\u4FE1\u7B49\u3002</p><p>\u5B58\u50A8\u578BXSS\u653B\u51FB\u5927\u81F4\u9700\u8981\u7ECF\u8FC7\u5982\u4E0B\u6B65\u9AA4:</p><ul><li>\u653B\u51FB\u8005\u5C06\u6076\u610F\u4EE3\u7801\u63D0\u4EA4\u5230\u2F6C\u6807\u2F79\u7AD9\u7684\u6570\u636E\u5E93\u4E2D\u3002</li><li>\u2F64\u6237\u6253\u5F00\u2F6C\u6807\u2F79\u7AD9\u65F6\uFF0C\u2F79\u7AD9\u670D\u52A1\u7AEF\u5C06\u6076\u610F\u4EE3\u7801\u4ECE\u6570\u636E\u5E93\u53D6\u51FA\uFF0C\u62FC\u63A5\u5728 HTML \u4E2D\u8FD4\u56DE\u7ED9\u6D4F\u89C8\u5668\u3002</li><li>\u2F64\u6237\u6D4F\u89C8\u5668\u63A5\u6536\u5230\u54CD\u5E94\u540E\u89E3\u6790\u6267\u2F8F\uFF0C\u6DF7\u5728\u5176\u4E2D\u7684\u6076\u610F\u4EE3\u7801\u4E5F\u88AB\u6267\u2F8F\u3002</li><li>\u6076\u610F\u4EE3\u7801\u7A83\u53D6\u2F64\u6237\u6570\u636E\u5E76\u53D1\u9001\u5230\u653B\u51FB\u8005\u7684\u2F79\u7AD9\uFF0C\u6216\u8005\u5192\u5145\u2F64\u6237\u7684\u2F8F\u4E3A\uFF0C\u8C03\u2F64\u2F6C\u6807\u2F79\u7AD9\u63A5\u2F1D\u6267\u2F8F\u653B\u51FB\u8005\u6307\u5B9A\u7684\u64CD\u4F5C\u3002</li></ul><p><img src="https://doc.panjingyi.top/blog/202207012150732.png" alt=""></p><h5 id="_2-\u53CD\u5C04\u578Bxss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_2-\u53CD\u5C04\u578Bxss\u653B\u51FB" aria-hidden="true">#</a> 2\uFF09\u53CD\u5C04\u578BXSS\u653B\u51FB</h5><p>\u53CD\u5C04\u578B\u6307\u7684\u662F\u653B\u51FB\u8005\u8BF1\u5BFC\u7528\u6237\u8BBF\u95EE\u4E00\u4E2A\u5E26\u6709\u6076\u610F\u4EE3\u7801\u7684 URL \u540E\uFF0C\u670D\u52A1\u5668\u7AEF\u63A5\u6536\u6570\u636E\u540E\u5904\u7406\uFF0C\u7136\u540E\u628A\u5E26\u6709\u6076\u610F\u4EE3\u7801\u7684\u6570\u636E\u53D1\u9001\u5230\u6D4F\u89C8\u5668\u7AEF\uFF0C\u6D4F\u89C8\u5668\u7AEF\u89E3\u6790\u8FD9\u6BB5\u5E26\u6709 XSS \u4EE3\u7801\u7684\u6570\u636E\u540E\u5F53\u505A\u811A\u672C\u6267\u884C\uFF0C\u6700\u7EC8\u5B8C\u6210 XSS \u653B\u51FB\u3002</p><p><img src="https://doc.panjingyi.top/blog/202207012150733.png" alt=""></p><p>\u53CD\u5C04\u578B <strong>XSS</strong> <strong>\u7684\u653B\u51FB\u6B65\u9AA4\uFF1A</strong></p><ol><li>\u653B\u51FB\u8005\u6784\u9020\u51FA\u7279\u6B8A\u7684 URL\uFF0C\u5176\u4E2D\u5305\u542B\u6076\u610F\u4EE3\u7801\u3002</li><li>\u2F64\u6237\u6253\u5F00\u5E26\u6709\u6076\u610F\u4EE3\u7801\u7684 URL \u65F6\uFF0C\u2F79\u7AD9\u670D\u52A1\u7AEF\u5C06\u6076\u610F\u4EE3\u7801\u4ECE URL \u4E2D\u53D6\u51FA\uFF0C\u62FC\u63A5\u5728 HTML \u4E2D\u8FD4\u56DE\u7ED9\u6D4F\u89C8\u5668\u3002</li><li>\u2F64\u6237\u6D4F\u89C8\u5668\u63A5\u6536\u5230\u54CD\u5E94\u540E\u89E3\u6790\u6267\u2F8F\uFF0C\u6DF7\u5728\u5176\u4E2D\u7684\u6076\u610F\u4EE3\u7801\u4E5F\u88AB\u6267\u2F8F\u3002</li><li>\u6076\u610F\u4EE3\u7801\u7A83\u53D6\u2F64\u6237\u6570\u636E\u5E76\u53D1\u9001\u5230\u653B\u51FB\u8005\u7684\u2F79\u7AD9\uFF0C\u6216\u8005\u5192\u5145\u2F64\u6237\u7684\u2F8F\u4E3A\uFF0C\u8C03\u2F64\u2F6C\u6807\u2F79\u7AD9\u63A5\u2F1D\u6267\u2F8F\u653B\u51FB\u8005\u6307\u5B9A\u7684\u64CD\u4F5C\u3002</li></ol><p>\u53CD\u5C04\u578B XSS \u6F0F\u6D1E\u5E38\u2EC5\u4E8E\u901A\u8FC7 URL \u4F20\u9012\u53C2\u6570\u7684\u529F\u80FD\uFF0C\u5982\u2F79\u7AD9\u641C\u7D22\u3001\u8DF3\u8F6C\u7B49\u3002 \u7531\u4E8E\u9700\u8981\u2F64\u6237\u4E3B\u52A8\u6253\u5F00\u6076\u610F\u7684 URL \u624D\u80FD\u2F63\u6548\uFF0C\u653B\u51FB\u8005\u5F80\u5F80\u4F1A\u7ED3\u5408\u591A\u79CD\u2F3F\u6BB5\u8BF1\u5BFC\u2F64\u6237\u70B9\u51FB\u3002</p><p>\u53CD\u5C04\u578B XSS \u8DDF\u5B58\u50A8\u578B XSS \u7684\u533A\u522B\u662F\uFF1A\u5B58\u50A8\u578B XSS \u7684\u6076\u610F\u4EE3\u7801\u5B58\u5728\u6570\u636E\u5E93\u2FA5\uFF0C\u53CD\u5C04\u578B XSS \u7684\u6076\u610F\u4EE3\u7801\u5B58\u5728 URL \u2FA5\uFF0CWeb\u670D\u52A1\u5668\u4E0D\u4F1A\u5B58\u50A8\u53CD\u5C04\u578BXSS\u653B\u51FB\u7684\u6076\u610F\u811A\u672C\u3002</p><h5 id="_3-\u57FA\u4E8Edom\u7684xss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_3-\u57FA\u4E8Edom\u7684xss\u653B\u51FB" aria-hidden="true">#</a> 3\uFF09\u57FA\u4E8EDOM\u7684XSS\u653B\u51FB</h5><p>DOM \u578B\u6307\u7684\u901A\u8FC7\u4FEE\u6539\u9875\u9762\u7684 DOM \u8282\u70B9\u5F62\u6210\u7684 XSS\u3002</p><p>\u57FA\u4E8EDOM\u7684XSS\u653B\u51FB\u662F\u4E0D\u4F1A\u7275\u6D89\u5230\u9875\u9762Web\u670D\u52A1\u5668\u7684\u3002\u5177\u4F53\u6765\u8BF4\uFF0C\u9ED1\u5BA2\u901A\u8FC7\u5404\u79CD\u624B\u6BB5\u5C06\u6076\u610F\u811A\u672C\u6CE8\u5165\u7528\u6237\u7684\u9875\u9762\u4E2D\uFF0C\u6BD4\u5982\u901A\u8FC7\u7F51\u7EDC\u52AB\u6301\u5728\u9875\u9762\uFF0C\u4FEE\u6539\u9875\u9762\u3002\u8FD9\u79CD\u52AB\u6301\u7C7B\u578B\u5F88\u591A\uFF0C\u6709\u901A\u8FC7WiFi\u8DEF\u7531\u5668\u52AB\u6301\u7684\uFF0C\u6709\u901A\u8FC7\u672C\u5730\u6076\u610F\u8F6F\u4EF6\u6765\u52AB\u6301\u7684\uFF0C\u5B83\u4EEC\u7684\u5171\u540C\u70B9\u662F\u5728<strong>Web\u8D44\u6E90\u4F20\u8F93\u8FC7\u7A0B\u6216\u8005\u5728\u7528\u6237\u4F7F\u7528\u9875\u9762\u7684\u8FC7\u7A0B\u4E2D</strong>\u4FEE\u6539Web\u9875\u9762\u7684\u6570\u636E\u3002</p><p>DOM<strong>\u578B</strong> <strong>XSS</strong> <strong>\u7684\u653B\u51FB\u6B65\u9AA4\uFF1A</strong></p><ol><li>\u653B\u51FB\u8005\u6784\u9020\u51FA\u7279\u6B8A\u7684 URL\uFF0C\u5176\u4E2D\u5305\u542B\u6076\u610F\u4EE3\u7801\u3002</li><li>\u2F64\u6237\u6253\u5F00\u5E26\u6709\u6076\u610F\u4EE3\u7801\u7684 URL\u3002</li><li>\u2F64\u6237\u6D4F\u89C8\u5668\u63A5\u6536\u5230\u54CD\u5E94\u540E\u89E3\u6790\u6267\u2F8F\uFF0C\u524D\u7AEF JavaScript \u53D6\u51FA URL \u4E2D\u7684\u6076\u610F\u4EE3\u7801\u5E76\u6267\u2F8F\u3002</li><li>\u6076\u610F\u4EE3\u7801\u7A83\u53D6\u2F64\u6237\u6570\u636E\u5E76\u53D1\u9001\u5230\u653B\u51FB\u8005\u7684\u2F79\u7AD9\uFF0C\u6216\u8005\u5192\u5145\u2F64\u6237\u7684\u2F8F\u4E3A\uFF0C\u8C03\u2F64\u2F6C\u6807\u2F79\u7AD9\u63A5\u2F1D\u6267\u2F8F\u653B\u51FB\u8005\u6307\u5B9A\u7684\u64CD\u4F5C\u3002</li></ol><p>\u5BF9\u4E8EDom\u578BXSS\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u7684\u66F4\u8BE6\u7EC6\u7684\u89E3\u91CA\u{1F449}\uFF0C<a href="https://blog.csdn.net/stpeace/article/details/107135942?ops_request_misc=%257B%2522request%255Fid%2522%253A%2522164799638416780274169272%2522%252C%2522scm%2522%253A%252220140713.130102334..%2522%257D&amp;request_id=164799638416780274169272&amp;biz_id=0&amp;utm_medium=distribute.pc_search_result.none-task-blog-2~all~sobaiduend~default-2-107135942.142%5Ev3%5Epc_search_quality_down,143%5Ev4%5Eregister&amp;utm_term=dom%E5%9E%8Bxss%E6%94%BB%E5%87%BB&amp;spm=1018.2226.3001.4187" target="_blank" rel="noopener noreferrer">Dom\u578BXSS\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\u548C\u9632\u5FA1</a></p><p>DOM \u578B XSS \u8DDF\u524D\u4E24\u79CD XSS \u7684\u533A\u522B\uFF1ADOM \u578B XSS \u653B\u51FB\u4E2D\uFF0C\u53D6\u51FA\u548C\u6267\u2F8F\u6076\u610F\u4EE3\u7801\u7531\u6D4F\u89C8\u5668\u7AEF\u5B8C\u6210\uFF0C\u5C5E\u4E8E\u524D\u7AEFJavaScript \u2F83\u8EAB\u7684\u5B89\u5168\u6F0F\u6D1E\uFF0C\u2F7D\u5176\u4ED6\u4E24\u79CD XSS \u90FD\u5C5E\u4E8E\u670D\u52A1\u7AEF\u7684\u5B89\u5168\u6F0F\u6D1E\u3002</p><h3 id="_3-\u5982\u4F55\u9632\u5FA1xss\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_3-\u5982\u4F55\u9632\u5FA1xss\u653B\u51FB" aria-hidden="true">#</a> 3.\u5982\u4F55\u9632\u5FA1XSS\u653B\u51FB</h3><p>\u5B58\u50A8\u578BXSS\u653B\u51FB\u548C\u53CD\u5C04\u578BXSS\u653B\u51FB\u90FD\u9700\u8981\u7ECF\u8FC7Web\u670D\u52A1\u5668\u6765\u5904\u7406\uFF0C\u56E0\u6B64\u53EF\u4EE5\u8BA4\u4E3A\u8FD9\u4E24\u79CD\u7C7B\u578B\u7684\u6F0F\u6D1E\u662F\u670D\u52A1\u7AEF\u7684\u5B89\u5168\u6F0F\u6D1E\u3002</p><p>\u800C\u57FA\u4E8EDOM\u7684XSS\u653B\u51FB\u5168\u90E8\u90FD\u662F\u5728\u6D4F\u89C8\u5668\u7AEF\u5B8C\u6210\u7684\uFF0C\u56E0\u6B64<strong>\u57FA\u4E8EDOM\u7684XSS\u653B\u51FB\u662F\u5C5E\u4E8E\u524D\u7AEF\u7684\u5B89\u5168\u6F0F\u6D1E\u3002</strong></p><ul><li><p>\u65E0\u8BBA\u662F\u4F55\u79CD\u7C7B\u578B\u7684XSS\u653B\u51FB\uFF0C\u5B83\u4EEC\u90FD\u662F\u8981\u9996\u5148<strong>\u5728\u6D4F\u89C8\u5668\u4E2D\u6CE8\u5165\u6076\u610F\u811A\u672C\uFF0C\u7136\u540E\u518D\u901A\u8FC7\u6076\u610F\u811A\u672C\u5C06\u7528\u6237\u4FE1\u606F\u53D1\u9001\u81F3\u9ED1\u5BA2\u90E8\u7F72\u7684\u6076\u610F\u670D\u52A1\u5668\u4E0A\u3002</strong></p><p>\u6240\u4EE5\u8981\u963B\u6B62XSS\u653B\u51FB\uFF0C\u6211\u4EEC\u53EF\u4EE5\u901A\u8FC7\u963B\u6B62<strong>\u6076\u610F\u811A\u672C\u7684\u6CE8\u5165</strong>\u548C<strong>\u6076\u610F\u6D88\u606F\u7684\u53D1\u9001</strong>\u6765\u5B9E\u73B0\u3002</p><p>\u670D\u52A1\u5668\u5BF9\u8F93\u5165\u7684\u811A\u672C\u8FDB\u884C\u8FC7\u6EE4\u548C\u8F6C\u7801</p><p>\u4E0D\u7BA1\u662F\u53CD\u5C04\u8FD8\u662F\u5B58\u50A8\u578BXSS\u653B\u51FB\uFF0C\u6211\u4EEC\u90FD\u53EF\u4EE5\u5728\u670D\u52A1\u5668\u5C06\u4E00\u4E9B\u5173\u952E\u7684\u5B57\u7B26\u8FDB\u884C\u8F6C\u7801\u3002</p><p>\u7ECF\u8FC7\u8F6C\u7801\u4E4B\u540E\u7684\u5185\u5BB9\uFF0C\u5373\u4F7F\u8FD4\u56DE\u9875\u9762\uFF0C\u811A\u672C\u4E5F\u4E0D\u4F1A\u6267\u884C\u3002</p></li><li><p>\u53EF\u4EE5\u4ECE\u6D4F\u89C8\u5668\u7684\u6267\u884C\u6765\u8FDB\u884C\u9884\u9632\uFF0C\u4E00\u79CD\u662F\u4F7F\u7528\u7EAF\u524D\u7AEF\u7684\u65B9\u5F0F\uFF0C\u4E0D\u7528\u670D\u52A1\u5668\u7AEF\u62FC\u63A5\u540E\u8FD4\u56DE\uFF08\u4E0D\u4F7F\u7528\u670D\u52A1\u7AEF\u6E32\u67D3\uFF09\u3002\u53E6\u4E00\u79CD\u662F\u5BF9\u9700\u8981\u63D2\u5165\u5230 HTML \u4E2D\u7684\u4EE3\u7801\u505A\u597D\u5145\u5206\u7684\u8F6C\u4E49\u3002\u5BF9\u4E8E DOM \u578B\u7684\u653B\u51FB\uFF0C\u4E3B\u8981\u662F\u524D\u7AEF\u811A\u672C\u7684\u4E0D\u53EF\u9760\u800C\u9020\u6210\u7684\uFF0C\u5BF9\u4E8E\u6570\u636E\u83B7\u53D6\u6E32\u67D3\u548C\u5B57\u7B26\u4E32\u62FC\u63A5\u7684\u65F6\u5019\u5E94\u8BE5\u5BF9\u53EF\u80FD\u51FA\u73B0\u7684\u6076\u610F\u4EE3\u7801\u60C5\u51B5\u8FDB\u884C\u5224\u65AD\u3002</p></li><li><p>\u4F7F\u7528 CSP \uFF0CCSP \u7684\u672C\u8D28\u662F\u5EFA\u7ACB\u4E00\u4E2A\u767D\u540D\u5355\uFF0C\u544A\u8BC9\u6D4F\u89C8\u5668\u54EA\u4E9B\u5916\u90E8\u8D44\u6E90\u53EF\u4EE5\u52A0\u8F7D\u548C\u6267\u884C\uFF0C\u4ECE\u800C\u9632\u6B62\u6076\u610F\u4EE3\u7801\u7684\u6CE8\u5165\u653B\u51FB\u3002</p><p>\u5B9E\u65BD\u4E25\u683C\u7684 CSP \u53EF\u4EE5\u6709\u6548\u5730\u9632\u8303 XSS \u653B\u51FB\uFF0C\u5177\u4F53\u6765\u8BB2 CSP \u6709\u5982\u4E0B\u51E0\u4E2A\u529F\u80FD\uFF1A</p><ul><li>\u9650\u5236\u52A0\u8F7D\u5176\u4ED6\u57DF\u4E0B\u7684\u8D44\u6E90\u6587\u4EF6\uFF0C\u8FD9\u6837\u5373\u4F7F\u9ED1\u5BA2\u63D2\u5165\u4E86\u4E00\u4E2A JavaScript \u6587\u4EF6\uFF0C\u8FD9\u4E2A JavaScript \u6587\u4EF6\u4E5F\u662F\u65E0\u6CD5\u88AB\u52A0\u8F7D\u7684\uFF1B</li><li>\u7981\u6B62\u5411\u7B2C\u4E09\u65B9\u57DF\u63D0\u4EA4\u6570\u636E\uFF0C\u8FD9\u6837\u7528\u6237\u6570\u636E\u4E5F\u4E0D\u4F1A\u5916\u6CC4\uFF1B</li><li>\u7981\u6B62\u6267\u884C\u5185\u8054\u811A\u672C\u548C\u672A\u6388\u6743\u7684\u811A\u672C\uFF1B</li><li>\u8FD8\u63D0\u4F9B\u4E86\u4E0A\u62A5\u673A\u5236\uFF0C\u8FD9\u6837\u53EF\u4EE5\u5E2E\u52A9\u6211\u4EEC\u5C3D\u5FEB\u53D1\u73B0\u6709\u54EA\u4E9B XSS \u653B\u51FB\uFF0C\u4EE5\u4FBF\u5C3D\u5FEB\u4FEE\u590D\u95EE\u9898\u3002</li></ul></li></ul><blockquote><ol><li>CSP \u6307\u7684\u662F\u5185\u5BB9\u5B89\u5168\u7B56\u7565\uFF0C\u5B83\u7684\u672C\u8D28\u662F\u5EFA\u7ACB\u4E00\u4E2A\u767D\u540D\u5355\uFF0C\u544A\u8BC9\u6D4F\u89C8\u5668\u54EA\u4E9B\u5916\u90E8\u8D44\u6E90\u53EF\u4EE5\u52A0\u8F7D\u548C\u6267\u884C\u3002\u6211\u4EEC\u53EA\u9700\u8981\u914D\u7F6E\u89C4\u5219\uFF0C\u5982\u4F55\u62E6\u622A\u7531\u6D4F\u89C8\u5668\u81EA\u5DF1\u6765\u5B9E\u73B0\u3002</li><li>\u901A\u5E38\u6709\u4E24\u79CD\u65B9\u5F0F\u6765\u5F00\u542F CSP\uFF0C\u4E00\u79CD\u662F\u8BBE\u7F6E HTTP \u9996\u90E8\u4E2D\u7684 Content-Security-Policy\uFF0C\u4E00\u79CD\u662F\u8BBE\u7F6E meta \u6807\u7B7E\u7684\u65B9\u5F0F <meta http-equiv="Content-Security-Policy"></li></ol></blockquote><ul><li>\u5BF9\u4E00\u4E9B\u654F\u611F\u4FE1\u606F\u8FDB\u884C\u4FDD\u62A4\uFF0C\u6BD4\u5982 cookie \u4F7F\u7528 http-only\uFF0C\u4F7F\u5F97\u811A\u672C\u65E0\u6CD5\u83B7\u53D6\u3002\u4E5F\u53EF\u4EE5\u4F7F\u7528\u9A8C\u8BC1\u7801\uFF0C\u907F\u514D\u811A\u672C\u4F2A\u88C5\u6210\u7528\u6237\u6267\u884C\u4E00\u4E9B\u64CD\u4F5C\u3002</li></ul><h2 id="csrf\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#csrf\u653B\u51FB" aria-hidden="true">#</a> CSRF\u653B\u51FB</h2><h3 id="_1-\u4EC0\u4E48\u662Fcsrf\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_1-\u4EC0\u4E48\u662Fcsrf\u653B\u51FB" aria-hidden="true">#</a> 1.\u4EC0\u4E48\u662FCSRF\u653B\u51FB</h3><p>CSRF \u653B\u51FB\u6307\u7684\u662F<strong>\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u653B\u51FB</strong>\uFF0C\u987E\u540D\u601D\u4E49\uFF0C\u662F\u4F2A\u9020\u8BF7\u6C42\uFF0C\u5192\u5145\u7528\u6237\u5728\u7AD9\u5185\u7684\u6B63\u5E38\u64CD\u4F5C\uFF1B\u653B\u51FB\u8005\u8BF1\u5BFC\u7528\u6237\u8FDB\u5165\u4E00\u4E2A\u7B2C\u4E09\u65B9\u7F51\u7AD9\uFF0C\u7136\u540E\u8BE5\u7F51\u7AD9\u5411\u88AB\u653B\u51FB\u7F51\u7AD9\u53D1\u9001\u8DE8\u7AD9\u8BF7\u6C42\u3002\u5982\u679C\u7528\u6237\u5728\u88AB\u653B\u51FB\u7F51\u7AD9\u4E2D\u4FDD\u5B58\u4E86\u767B\u5F55\u72B6\u6001\uFF0C\u90A3\u4E48\u653B\u51FB\u8005\u5C31\u53EF\u4EE5\u5229\u7528\u8FD9\u4E2A\u767B\u5F55\u72B6\u6001\uFF0C\u7ED5\u8FC7\u540E\u53F0\u7684\u7528\u6237\u9A8C\u8BC1\uFF0C\u5192\u5145\u7528\u6237\u5411\u670D\u52A1\u5668\u6267\u884C\u4E00\u4E9B\u64CD\u4F5C\u3002</p><p>\u4E3E\u4F8B\uFF1A</p><p>\u200B \u7528\u6237\u767B\u5F55\u5FAE\u535A\u8D26\u53F7\uFF0C\u8FD9\u65F6\u9ED1\u5BA2\u7ED9\u4F60\u4E00\u4E2A\u7F8E\u5973\u56FE\u7247\u7684\u94FE\u63A5\uFF0C\u4F60\u70B9\u51FB\u4E4B\u540E\u8FDB\u5165\u9ED1\u5BA2\u7684\u7F51\u7AD9\uFF0C\u4ED6\u83B7\u53D6\u4F60\u7684\u767B\u5F55\u72B6\u6001\uFF0C\u5229\u7528\u4F60\u7684\u8D26\u53F7\u4F20\u64AD\u4E00\u4E9B\u4E71\u4E03\u516B\u7CDF\u7684\u4FE1\u606F\u3002</p><p>\u548CXSS\u4E0D\u540C\u7684\u662F\uFF0CCSRF\u653B\u51FB\u4E0D\u9700\u8981\u5C06\u6076\u610F\u4EE3\u7801\u6CE8\u5165\u7528\u6237\u7684\u9875\u9762\uFF0C\u4EC5\u4EC5\u662F\u5229\u7528\u670D\u52A1\u5668\u7684\u6F0F\u6D1E\u548C\u7528\u6237\u7684\u767B\u5F55\u72B6\u6001\u6765\u5B9E\u65BD\u653B\u51FB\u3002</p><p>\u4F60\u8FD9\u53EF\u4EE5\u8FD9\u4E48\u7406\u89E3CSRF\u653B\u51FB\uFF1A\u653B\u51FB\u8005\u76D7\u7528\u4E86\u4F60\u7684\u8EAB\u4EFD\uFF0C\u4EE5\u4F60\u7684\u540D\u4E49\u53D1\u9001\u6076\u610F\u8BF7\u6C42\u3002CSRF\u80FD\u591F\u505A\u7684\u4E8B\u60C5\u5305\u62EC\uFF1A\u4EE5\u4F60\u540D\u4E49\u53D1\u9001\u90AE\u4EF6\uFF0C\u53D1\u6D88\u606F\uFF0C\u76D7\u53D6\u4F60\u7684\u8D26\u53F7\uFF0C\u751A\u81F3\u4E8E\u8D2D\u4E70\u5546\u54C1\uFF0C\u865A\u62DF\u8D27\u5E01\u8F6C\u8D26......\u9020\u6210\u7684\u95EE\u9898\u5305\u62EC\uFF1A\u4E2A\u4EBA\u9690\u79C1\u6CC4\u9732\u4EE5\u53CA\u8D22\u4EA7\u5B89\u5168\u3002</p><h3 id="_2-\u653B\u51FB\u7C7B\u578B-1" tabindex="-1"><a class="header-anchor" href="#_2-\u653B\u51FB\u7C7B\u578B-1" aria-hidden="true">#</a> 2.\u653B\u51FB\u7C7B\u578B</h3><p>\u5E38\u89C1\u7684 CSRF \u653B\u51FB\u6709\u4E09\u79CD\uFF1A</p><ul><li>GET \u7C7B\u578B\u7684 CSRF \u653B\u51FB\uFF0C\u6BD4\u5982\u5728\u7F51\u7AD9\u4E2D\u7684\u4E00\u4E2A img \u6807\u7B7E\u91CC\u6784\u5EFA\u4E00\u4E2A\u8BF7\u6C42\uFF0C\u5F53\u7528\u6237\u6253\u5F00\u8FD9\u4E2A\u7F51\u7AD9\u7684\u65F6\u5019\u5C31\u4F1A\u81EA\u52A8\u53D1\u8D77\u63D0\u4EA4\u3002</li><li>POST \u7C7B\u578B\u7684 CSRF \u653B\u51FB\uFF0C\u6BD4\u5982\u6784\u5EFA\u4E00\u4E2A\u8868\u5355\uFF0C\u7136\u540E\u9690\u85CF\u5B83\uFF0C\u5F53\u7528\u6237\u8FDB\u5165\u9875\u9762\u65F6\uFF0C\u81EA\u52A8\u63D0\u4EA4\u8FD9\u4E2A\u8868\u5355\u3002</li><li>\u94FE\u63A5\u7C7B\u578B\u7684 CSRF \u653B\u51FB\uFF0C\u6BD4\u5982\u5728 a \u6807\u7B7E\u7684 href \u5C5E\u6027\u91CC\u6784\u5EFA\u4E00\u4E2A\u8BF7\u6C42\uFF0C\u7136\u540E\u8BF1\u5BFC\u7528\u6237\u53BB\u70B9\u51FB\u3002</li></ul><h3 id="_3-\u5982\u4F55\u9632\u5FA1-csrf-\u653B\u51FB" tabindex="-1"><a class="header-anchor" href="#_3-\u5982\u4F55\u9632\u5FA1-csrf-\u653B\u51FB" aria-hidden="true">#</a> 3.\u5982\u4F55\u9632\u5FA1 CSRF \u653B\u51FB\uFF1F</h3><p>\u53D1\u8D77CSRF\u653B\u51FB\u7684\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6</p><p>1\u3001\u76EE\u6807\u7AD9\u70B9\u4E00\u5B9A\u8981\u6709CSRF<strong>\u6F0F\u6D1E</strong></p><p>2\u3001\u7528\u6237\u8981\u767B\u5F55\u8FC7\u76EE\u6807\u7AD9\u70B9\uFF0C\u5E76\u4E14\u5728\u6D4F\u89C8\u5668\u4E0A<strong>\u4FDD\u6301\u6709\u8BE5\u7AD9\u70B9\u7684\u767B\u5F55\u72B6\u6001\u3002</strong></p><p>3\u3001\u7528\u6237\u9700\u8981\u6253\u5F00\u7B2C\u4E09\u65B9\u7AD9\u70B9\uFF0C\u53EF\u4EE5\u662F\u9ED1\u5BA2\u7F51\u7AD9\uFF0C\u4E5F\u53EF\u4EE5\u662F\u4E00\u4E9B\u8BBA\u575B\u3002</p><p>\u6EE1\u8DB3\u4E09\u4E2A\u6761\u4EF6\uFF0C\u9ED1\u5BA2\u5C31\u53EF\u4EE5\u8FDB\u884CCSRF\u653B\u51FB\u4E86\uFF0C\u8FD9\u91CC\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u4E0EXSS\u4E0D\u540C\uFF0CCSRF\u653B\u51FB\u4E0D\u9700\u8981\u6CE8\u5165\u811A\u672C\uFF0C\u56E0\u6B64\u9ED1\u5BA2\u65E0\u6CD5\u901A\u8FC7CSRF\u653B\u51FB\u6765\u83B7\u53D6\u7528\u6237\u9875\u9762\u6570\u636E\uFF1B\u5176\u6700\u5173\u952E\u4E00\u70B9\u662F\u8981\u627E\u5230\u670D\u52A1\u5668\u7684\u6F0F\u6D1E\uFF0C\u6240\u4EE5\u8BF4\u5BF9\u4E8ECSRF\u653B\u51FB\u6211\u4EEC\u4E3B\u8981\u7684\u9632\u62A4\u624B\u6BB5\u662F\u63D0\u5347\u670D\u52A1\u5668\u7684\u5B89\u5168\u6027\u3002</p><p><strong>CSRF \u653B\u51FB\u53EF\u4EE5\u4F7F\u7528\u4EE5\u4E0B\u65B9\u6CD5\u6765\u9632\u62A4\uFF1A</strong></p><ul><li><strong>\u8FDB\u884C\u540C\u6E90\u68C0\u6D4B</strong>\uFF0C\u670D\u52A1\u5668\u6839\u636E http \u8BF7\u6C42\u5934\u4E2D origin \u6216\u8005 referer \u4FE1\u606F\u6765\u5224\u65AD\u8BF7\u6C42\u662F\u5426\u4E3A\u5141\u8BB8\u8BBF\u95EE\u7684\u7AD9\u70B9\uFF0C\u4ECE\u800C\u5BF9\u8BF7\u6C42\u8FDB\u884C\u8FC7\u6EE4\u3002\u5F53 origin \u6216\u8005 referer \u4FE1\u606F\u90FD\u4E0D\u5B58\u5728\u7684\u65F6\u5019\uFF0C\u76F4\u63A5\u963B\u6B62\u8BF7\u6C42\u3002\u8FD9\u79CD\u65B9\u5F0F\u7684\u7F3A\u70B9\u662F\u6709\u4E9B\u60C5\u51B5\u4E0B referer \u53EF\u4EE5\u88AB\u4F2A\u9020\uFF0C\u540C\u65F6\u8FD8\u4F1A\u628A\u641C\u7D22\u5F15\u64CE\u7684\u94FE\u63A5\u4E5F\u7ED9\u5C4F\u853D\u4E86\u3002\u6240\u4EE5\u4E00\u822C\u7F51\u7AD9\u4F1A\u5141\u8BB8\u641C\u7D22\u5F15\u64CE\u7684\u9875\u9762\u8BF7\u6C42\uFF0C\u4F46\u662F\u76F8\u5E94\u7684\u9875\u9762\u8BF7\u6C42\u8FD9\u79CD\u8BF7\u6C42\u65B9\u5F0F\u4E5F\u53EF\u80FD\u88AB\u653B\u51FB\u8005\u7ED9\u5229\u7528\u3002\uFF08Referer \u5B57\u6BB5\u4F1A\u544A\u8BC9\u670D\u52A1\u5668\u8BE5\u7F51\u9875\u662F\u4ECE\u54EA\u4E2A\u9875\u9762\u94FE\u63A5\u8FC7\u6765\u7684\uFF09</li><li><strong>\u4F7F\u7528 CSRF Token \u8FDB\u884C\u9A8C\u8BC1</strong>\uFF0C\u670D\u52A1\u5668\u5411\u7528\u6237\u8FD4\u56DE\u4E00\u4E2A\u968F\u673A\u6570 Token \uFF0C\u5F53\u7F51\u7AD9\u518D\u6B21\u53D1\u8D77\u8BF7\u6C42\u65F6\uFF0C\u5728\u8BF7\u6C42\u53C2\u6570\u4E2D\u52A0\u5165\u670D\u52A1\u5668\u7AEF\u8FD4\u56DE\u7684 token \uFF0C\u7136\u540E\u670D\u52A1\u5668\u5BF9\u8FD9\u4E2A token \u8FDB\u884C\u9A8C\u8BC1\u3002\u8FD9\u79CD\u65B9\u6CD5\u89E3\u51B3\u4E86\u4F7F\u7528 cookie \u5355\u4E00\u9A8C\u8BC1\u65B9\u5F0F\u65F6\uFF0C\u53EF\u80FD\u4F1A\u88AB\u5192\u7528\u7684\u95EE\u9898\uFF0C\u4F46\u662F\u8FD9\u79CD\u65B9\u6CD5\u5B58\u5728\u4E00\u4E2A\u7F3A\u70B9\u5C31\u662F\uFF0C\u6211\u4EEC\u9700\u8981\u7ED9\u7F51\u7AD9\u4E2D\u7684\u6240\u6709\u8BF7\u6C42\u90FD\u6DFB\u52A0\u4E0A\u8FD9\u4E2A token\uFF0C\u64CD\u4F5C\u6BD4\u8F83\u7E41\u7410\u3002\u8FD8\u6709\u4E00\u4E2A\u95EE\u9898\u662F\u4E00\u822C\u4E0D\u4F1A\u53EA\u6709\u4E00\u53F0\u7F51\u7AD9\u670D\u52A1\u5668\uFF0C\u5982\u679C\u8BF7\u6C42\u7ECF\u8FC7\u8D1F\u8F7D\u5E73\u8861\u8F6C\u79FB\u5230\u4E86\u5176\u4ED6\u7684\u670D\u52A1\u5668\uFF0C\u4F46\u662F\u8FD9\u4E2A\u670D\u52A1\u5668\u7684 session \u4E2D\u6CA1\u6709\u4FDD\u7559\u8FD9\u4E2A token \u7684\u8BDD\uFF0C\u5C31\u6CA1\u6709\u529E\u6CD5\u9A8C\u8BC1\u4E86\u3002\u8FD9\u79CD\u60C5\u51B5\u53EF\u4EE5\u901A\u8FC7\u6539\u53D8 token \u7684\u6784\u5EFA\u65B9\u5F0F\u6765\u89E3\u51B3\u3002</li><li><strong>\u5BF9</strong> <strong>Cookie \u8FDB\u884C</strong>\u53CC\u91CD\u9A8C\u8BC1\uFF0C\u670D\u52A1\u5668\u5728\u7528\u6237\u8BBF\u95EE\u7F51\u7AD9\u9875\u9762\u65F6\uFF0C\u5411\u8BF7\u6C42\u57DF\u540D\u6CE8\u5165\u4E00\u4E2ACookie\uFF0C\u5185\u5BB9\u4E3A\u968F\u673A\u5B57\u7B26\u4E32\uFF0C\u7136\u540E\u5F53\u7528\u6237\u518D\u6B21\u5411\u670D\u52A1\u5668\u53D1\u9001\u8BF7\u6C42\u7684\u65F6\u5019\uFF0C\u4ECE cookie \u4E2D\u53D6\u51FA\u8FD9\u4E2A\u5B57\u7B26\u4E32\uFF0C\u6DFB\u52A0\u5230 URL \u53C2\u6570\u4E2D\uFF0C\u7136\u540E\u670D\u52A1\u5668\u901A\u8FC7\u5BF9 cookie \u4E2D\u7684\u6570\u636E\u548C\u53C2\u6570\u4E2D\u7684\u6570\u636E\u8FDB\u884C\u6BD4\u8F83\uFF0C\u6765\u8FDB\u884C\u9A8C\u8BC1\u3002\u4F7F\u7528\u8FD9\u79CD\u65B9\u5F0F\u662F\u5229\u7528\u4E86\u653B\u51FB\u8005\u53EA\u80FD\u5229\u7528 cookie\uFF0C\u4F46\u662F\u4E0D\u80FD\u8BBF\u95EE\u83B7\u53D6 cookie \u7684\u7279\u70B9\u3002\u5E76\u4E14\u8FD9\u79CD\u65B9\u6CD5\u6BD4 CSRF Token \u7684\u65B9\u6CD5\u66F4\u52A0\u65B9\u4FBF\uFF0C\u5E76\u4E14\u4E0D\u6D89\u53CA\u5230\u5206\u5E03\u5F0F\u8BBF\u95EE\u7684\u95EE\u9898\u3002\u8FD9\u79CD\u65B9\u6CD5\u7684\u7F3A\u70B9\u662F\u5982\u679C\u7F51\u7AD9\u5B58\u5728 XSS \u6F0F\u6D1E\u7684\uFF0C\u90A3\u4E48\u8FD9\u79CD\u65B9\u5F0F\u4F1A\u5931\u6548\u3002\u540C\u65F6\u8FD9\u79CD\u65B9\u5F0F\u4E0D\u80FD\u505A\u5230\u5B50\u57DF\u540D\u7684\u9694\u79BB\u3002</li><li><strong>\u5728\u8BBE\u7F6E cookie \u5C5E\u6027\u7684\u65F6\u5019\u8BBE\u7F6E Samesite \uFF0C\u9650\u5236 cookie \u4E0D\u80FD\u4F5C\u4E3A\u88AB\u7B2C\u4E09\u65B9\u4F7F\u7528</strong>\uFF0C\u4ECE\u800C\u53EF\u4EE5\u907F\u514D\u88AB\u653B\u51FB\u8005\u5229\u7528\u3002Samesite \u4E00\u5171\u6709\u4E24\u79CD\u6A21\u5F0F\uFF0C\u4E00\u79CD\u662F\u4E25\u683C\u6A21\u5F0F\uFF0C\u5728\u4E25\u683C\u6A21\u5F0F\u4E0B cookie \u5728\u4EFB\u4F55\u60C5\u51B5\u4E0B\u90FD\u4E0D\u53EF\u80FD\u4F5C\u4E3A\u7B2C\u4E09\u65B9 Cookie \u4F7F\u7528\uFF0C\u5728\u5BBD\u677E\u6A21\u5F0F\u4E0B\uFF0Ccookie \u53EF\u4EE5\u88AB\u8BF7\u6C42\u662F GET \u8BF7\u6C42\uFF0C\u4E14\u4F1A\u53D1\u751F\u9875\u9762\u8DF3\u8F6C\u7684\u8BF7\u6C42\u6240\u4F7F\u7528\u3002</li></ul><p>\u5173\u4E8ECSRF\u653B\u51FB\u9632\u62A4\u7684\u66F4\u8BE6\u7EC6\u7684\u89E3\u91CA\uFF0C<a href="https://blog.csdn.net/weixin_52834435/article/details/123674736?spm=1001.2014.3001.5501" target="_blank" rel="noopener noreferrer">\u70B9\u51FB\u8FD9\u91CC\u{1F449}</a>\uFF0C\u5728\u8FD9\u7BC7\u6587\u7AE0\u7684\u7B2C\u4E94\u3001\u516D\u70B9\u3002</p>',56),a=[s];function l(n,p){return i(),r("div",null,a)}var c=e(t,[["render",l],["__file","\u6D4F\u89C8\u5668\u5B89\u5168.html.vue"]]);export{c as default};
